import React,{useState}from 'react';
import { StyleSheet, Text,TextInput, View,ScrollView,Image,Dimensions  } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';
export default function Search() {
    const [val,setVal]=useState('')


    return (
        <View style={styles.container} >
                <View style={{height:65,color:"white",margin:0,padding:10,display:'flex',flexDirection:"row",backgroundColor:"black",justifyContent:"space-between",alignItems:"center",levation:4,}}>

             <MaterialIcons name="arrow-back" size={35} color="white" />
             <TextInput
             style={{backgroundColor:"#232323",width:"70%",margin:0,padding:8,borderRadius:10,color:"white",fontSize:16}}
             value={val}
             onChangeText={(text)=>setVal(text)}
             placeholder="Search Here..."
             />

             <MaterialIcons name="send" size={35} color="white" />
        </View>


        <View  style={{elevation:3,margin:10, backgroundColor:"gray",display:"flex",flexDirection:"row",}}>
            <Image 
            source={{uri:"https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTMqSZ5J8sMojfz--IFIHPcGlClswfexRSRcg&usqp=CAU"}}
            style={{
                width:"40%",
                height:180,
                backgroundColor:"red"
            }}

            />
                <View style={{margin:10,width:"60%"}}>

                     <View style={{marginLeft:10,display:"flex",flexDirection:"column"}}>
                      <Text  style={{fontSize:16,color:"white",width:Dimensions.get('screen').width-80}} ellipsizeMode="tail" numberOfLines={2}>
                        Definition and Usage. The background-image property sets one or more background images for an element
                      </Text>
                    <Text style={{fontSize:12,width:'80%',color:"white"}}>Define The background property.</Text>
            </View>
        </View>



        </View>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: 'white',
      marginTop:30
    },
  });
